      "original": ["Introduction", "Search strategy and selection criteria ::: Data and Methods", "Search strategy and selection criteria ::: Data and Methods", "Search strategy and selection criteria ::: Data and Methods", "Data Extraction and Quality Assessment ::: Data and Methods", "Statistical Analysis of Data ::: Data and Methods", "Research Selection and Quality Assessment ::: Results", "Demographical characteristics ::: Results", "Demographical characteristics ::: Results", "Demographical characteristics ::: Results", "Demographical characteristics ::: Results", "Comorbidities ::: Results", "Clinical Manifestation ::: Results", "Laboratory examination ::: Results", "Discussion", "Discussion", "Discussion", "Discussion", "Discussion", "Discussion", "Discussion", "Discussion"],
      "generic": ["introduction", "method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "conclusions", "conclusions", "conclusions", "conclusions", "conclusions", "conclusions", "conclusions", "conclusions"]
