const similar_paper_3_2 = [{
  paper_id: "PMC7115413",
  doi: "10.1016/j.ejmech.2016.05.027",
  title: "Enfuvirtide−PEG conjugate: A potent HIV fusion inhibitor with improved pharmacokinetic properties",
  doc_date: "2016-10-04",
  authors: ["Shuihong.Cheng", "Yan.Wang", "Zhenxing.Zhang", "Xun.Lv", "George.Gao", "Yiming.Shao", "Liying.Ma", "Xuebing.Li"],
  summary: "",
  abstract: {
