      original: ["Study Design and Settings ::: MATERIALS AND METHODS", "Study Design and Settings ::: MATERIALS AND METHODS", "Study Design and Settings ::: MATERIALS AND METHODS", "Study Design and Settings ::: MATERIALS AND METHODS", "Sample Collection and Laboratory Procedures ::: MATERIALS AND METHODS", "Sample Collection and Laboratory Procedures ::: MATERIALS AND METHODS", "Sample Collection and Laboratory Procedures ::: MATERIALS AND METHODS", "Sample Collection and Laboratory Procedures ::: MATERIALS AND METHODS", "Sample Collection and Laboratory Procedures ::: MATERIALS AND METHODS", "Data Management and Statistical Analysis ::: MATERIALS AND METHODS", "Demographic and Clinical Characteristics of Patients With ILI ::: RESULTS", "Demographic and Clinical Characteristics of Patients With ILI ::: RESULTS", "Virus Detection ::: RESULTS", "Virus Detection ::: RESULTS", "Virus Detection ::: RESULTS", "Virus Detection ::: RESULTS", "Virus Detection ::: RESULTS", "Virus Detection ::: RESULTS", "Seasonality ::: RESULTS", "DISCUSSION", "DISCUSSION", "DISCUSSION", "DISCUSSION", "DISCUSSION", "DISCUSSION", "DISCUSSION", "DISCUSSION"],
      generic: ["method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "method", "conclusions", "conclusions", "conclusions", "conclusions", "conclusions", "conclusions", "conclusions", "conclusions"]
