      generic: ["introduction", "introduction", "introduction", "method", "method", "method", "method", "categories-and-subject-descriptors", "categories-and-subject-descriptors", "categories-and-subject-descriptors", "method", "method", "method", "method", "method", "method", "method", "method", "conclusions", "conclusions"]
