import Dropdown from "react-dropdown";

export default function ArticleDetailsSimilarPaper(props) {
  //dinamic similar articles, comment out &set correct url to use
  var [similar, setSimilar] = useState([]);
  const {
    paper_id,
    doi,
    doc_date,
    title,
    authors,
    summary,
    abstract,
    bodyText,
