  {
    paper_id: "PMC7138107",
    doi: "10.1016/s0140-6736(06)68624-8",
    title: "Poliomyelitis eradication and pandemic influenza",
    doc_date: "2006-05-04",
    authors: ["David.Heymann", "R.Aylward"],
    summary: "",
    abstract: { text: [], tags: { sciwing: [] } },
    bodyText: {
      section_header: {
        original: ["", "", "", "", "", "", "", ""],
        generic: ["", "", "", "", "", "", "", ""]
