        authors: ["Wan.Park", "*.Kang", "Il.Jun", "Gayeon.Kim", "Jae-Phil.Choi", "Ji-Young.Rhee", "Shinhyea.Cheon", "Chang.Lee", "Jun-Sun.Park", "Yeonjae.Kim", "Joon-Sung.Joh", "Bum.Chin", "Pyeong.Choe", "Ji.Bang", "Sang-Won.Park", "Nam.Kim", "Dong-Gyun.Lim", "Yeon-Sook.Kim", "Myoung-Don.Oh", "Hyoung-Shik.Shin"],
