        {
          paper_id: "96a2891a55f0d6076cd89b09e8571d4ce262fbc7",
          doi: "10.1101/2020.04.23.20076513",
          title:
            "Molecular Epidemiology of Paramyxoviruses in Frugivorous Eidolon helvum Bats in Zambia",
          doc_date: "doc_date",
          authors: [
            "Walter.Muleya",
            "Michihito.Sasaki",
            "Yasuko.Orba",
            "Akihiro.Ishii",
            "Yuka.Thomas",
            "Emiko.Nakagawa",
