import React, { useState, useEffect } from "react";
import Axios from "axios";
import Article from "./Article";
import Header from "./Header";
import Collapsible from "react-collapsible";

function SearchMain() {
  const [query, setQuery] = useState("");
  const [articles, setArticles] = useState([]);
  const [alert, setAlert] = useState("");

  //url for search function
  const url = `http://localhost:8000/answer/?query=${query}`;

  const getData = async () => {
    if (query !== "") {
      const result = await Axios.get(url);
      if (!result.data) {
        return setAlert("No article retrieved for this query.");
      }
      console.log(result);
      setArticles(result.data);
      setQuery("");
      setAlert("");
    } else {
      setAlert("Please fill the form");
    }
  };

  const onChange = e => setQuery(e.target.value);

  const onSubmit = e => {
    e.preventDefault();
    getData();
  };

  return (
    <div className="App-header">
      <Header />
      <form onSubmit={onSubmit} className="search-form" htmlFor="search-input">
        <input
          type="text"
          name="query"
          value={query}
          id="search-input"
          // placeholder="Search..."
          placeholder="What are the risk factors of covid 19?"
          //placeholder="Is there a cure for Covid-19?"
          //placeholder="How long will the outbreak of Covid-19 last?"
          autoComplete="off"
          onChange={onChange}
        />
        <input type="submit" value="Search" />
      </form>

      <ul className="articles">
        {/* <div className="query_display">
          <h3>Query:&nbsp;{query}</h3>
        </div> */}
        {articleSample !== [] &&
          articleSample.map(article => (
            <Article key={article.paper_id} article={article} />
          ))}
      </ul>
      <ul className="articles">
        {/* <div className="query_display">
          <h3>Query:&nbsp;{query}</h3>
        </div> */}
        {articles !== [] &&
          articles.map(article => (
            <Article key={article.paper_id} article={article} />
          ))}
      </ul>
    </div>
  );
}

export default SearchMain;

